import React, {useState} from 'react';

const AreaNuevaNota = (props) => {
    //State de los input ingresados ne la nueva nota
    const [input, setInput] = useState({
        titulo: '',
        contenido: '',
    });

    function manejoCambio(evento) {
        const {name, value} = evento.target;
        
        setInput( (valorPrevio) => {
            if (name === 'titulo')
                return {
                    titulo: value,
                    contenido: valorPrevio.contenido,
                }
            else
                return {
                    titulo: valorPrevio.titulo,
                    contenido: value,
                }
        });
    }

    function manejoClick() {
        //TOMARA INPUT 
        //LO MANDARA AL MANEJO DE STATE DE LISTANOTA 
        props.setearNota(input)
        //SE RESETEARA EL INPUT INGRESADO 
        setInput({titulo: '', contenido: ''})
    }
    
    return ( 
        <div>
            <form>
                <input 
                    onChange={manejoCambio}
                    value={input.titulo}
                    className="tituloNuevaNota" 
                    name="titulo" 
                    placeholder="Titulo"
                 />
                <textarea 
                    onChange={manejoCambio}
                    value={input.contenido}
                    name="contenido" 
                    placeholder="Escriba una nota..." 
                    rows="3"
                 />
                <button 
                    className="btn-agregar"
                    onClick={manejoClick}
                >+</button>
            </form>
        </div>
    );
}
 
export default AreaNuevaNota;
